export const signing_in = ({email, password}) => async (dispatch) => {
    try {
        let response = await axios.post('http://192.168.1.108:3000/login/customer', {email, password})

        await dispatch ({
            type: "SIGNIN_USER",
            payload: response.data.token
        })
        return response.data.token;
    }

    catch(error) {
        console.log(error);
        return ({error: error})
    }
}
