import axios from "axios"
import React, { useEffect } from "react"
import {Text, View, TextInput, TouchableOpacity, StyleSheet, SafeAreaView, Alert} from "react-native"
import AsyncStorage from '@react-native-community/async-storage'
import { useState } from "react"

export default function LogInForm( {authState, logIn, navigation} ) {
    const [email, setEmail] = useState("")
    const [password, setPassword] = useState("")
    
    /*const initialEmail = {
      email: ""
    }

    const initialPassword = {
      password: ""
    }*/

    const handleLogIn = async () => {
      if (authState.loading) {
        console.log("Loading")
      }
      else if (authState.error) {
          console.log(authState.error)
      }
      else {
          logIn(email, password)
          navigation.navigate("Speakers")
      }
    }



    return (
    <SafeAreaView style = {styles.container}>
    <Text style = {styles.logo}>Repair Dukaan</Text>
      <View style = {styles.inputView}>
          <TextInput  
            style={styles.inputText}
            placeholder="Email" 
            placeholderTextColor="#003f5c"
            onChangeText = {setEmail}
            keyboardType = 'email-address'
          />
      </View>
      <View style = {styles.inputView}>
          <TextInput  
            style={styles.inputText}
            placeholder="Password" 
            placeholderTextColor="#003f5c"
            secureTextEntry = {true}
            onChangeText = {setPassword}
          />
      </View>
      <TouchableOpacity style={styles.loginBtn} onPress = {handleLogIn}>
          <Text style={styles.loginText}>LOGIN</Text>
      </TouchableOpacity>

      <TouchableOpacity onPress = {() => navigation.navigate('RegisterScreen')}>
          <Text style={styles.loginText}>Sign Up</Text>
      </TouchableOpacity>
      </SafeAreaView>
    )
}


const mapStateToProps = state => {
  return {
      authState: state
  }
}

const mapDispatchToProps = (dispatch, email, password) => {
  return {
      logIn: () => dispatch(signIn(email, password))
  }
}


const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#003f5c',
        alignItems: 'center',
        justifyContent: 'center',
    },
    logo: {
        fontWeight:"bold",
        fontSize:50,
        color:"#fb5b5a",
        marginBottom:40,
      },
      inputView:{
        width:"80%",
        backgroundColor:"#465881",
        borderRadius:25,
        height:50,
        marginBottom:20,
        justifyContent:"center",
        padding:20
      },
      inputText:{
        height:50,
        color:"white"
      },
      loginBtn:{
        width:"80%",
        backgroundColor:"#fb5b5a",
        borderRadius:25,
        height:50,
        alignItems:"center",
        justifyContent:"center",
        marginTop:30,
        marginBottom:20
      },
      loginText:{
        color:"white"
      }
})
