  <MapStack.Navigator screenOptions={{
          headerStyle: {
          backgroundColor: '#009387',
          },
          headerTintColor: '#fff',
          headerTitleStyle: {
          fontWeight: 'bold'
          }
      }}>
          <MapStack.Screen name="Map" component={Map} options={{
          title:'         RepairDukaan',
          }} />
                <MapStack.Screen name="Tracker"  component = {Tracker} options={{
          title:'         RepairDukaan',
          }} />
          <MapStack.Screen name="Inprogress"  component = {Inprogress} options={{
          title:'         RepairDukaan',
          }} />
  </MapStack.Navigator>
  );*/



function HomeStack({ navigation }) {
  return (
    <Stack.Navigator
      screenOptions={{
        headerLeft: () => (
          <NavigationDrawerStructure navigationProps={navigation} />
        ),
        headerStyle: {
          backgroundColor: '#f4511e', //Set Header color
        },
        headerTintColor: '#fff', //Set Header text color
        headerTitleStyle: {
          fontWeight: 'bold', //Set Header text style
          textAlign : 'center', //Set Header text style
          fontSize: 42
        },
      }}>
      <Stack.Screen
        name="Home"
        component={Home}
        options={{
          title: 'REPAIR DUKAAN', //Set Header Title
        }}
      />
    </Stack.Navigator>
  );
}






export default function DrawerNagivation() {
  return (
     <SafeAreaProvider>
      <Drawer.Navigator initialRouteName = {Home} drawerContent = {props => {
         return (
          <DrawerContentScrollView {...props}>
            <DrawerItemList {...props} />
            <DrawerItem label="Logout" onPress={() => props.navigation.navigate("LogIn")} />
          </DrawerContentScrollView>
        )
      }}>
        <Drawer.Screen
          name="Home"
          options={{ drawerLabel: 'Home' }}
          component={HomeStack}
        />
        <Drawer.Screen
          name="Rates"
          options={{ drawerLabel: 'Rates' }}
          component={RatesStack}
        />
        <Drawer.Screen
          name="Report"
          options={{ drawerLabel: 'Report' }}
          component={ReportStack}
        />
        <Drawer.Screen
          name="HowToUse"
          options={{ drawerLabel: 'Help' }}
          component={HowToUseStack}
        />
        <Drawer.Screen
          name="Contact"
          options={{ drawerLabel: 'Contact' }}
          component={ContactStack}
        />
        {/*(<Drawer.Screen
          name = "Map"
          options = {{drawerLabel: 'Map'}}
          component = {MapStackScreen}
        />*/}
        
      </Drawer.Navigator>    
  
       
      
    
     </SafeAreaProvider>  
  

  );
  
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  button: {
    backgroundColor: "blue",
    padding: 12,
    borderRadius: 60,
    marginVertical: 20,
  },
  buttonText: {
    fontSize: 20,
    color: '#fff',
    fontFamily: 'normal',
  }, 
});
