import axios from "axios"
import React from "react"
import {Text, View, TextInput, TouchableOpacity, StyleSheet, SafeAreaView, Alert} from "react-native"
import { useState } from "react"
import Profile from './Profile.js'

export default function LogInForm() {
    const [email, setEmail] = useState("Email")
    const [password, setPassword] = useState("Password")

    const handleLogIn = async () => {
        await axios.post('http://192.168.1.108:3000/login/customer', {email, password})
            .then((response) => {
                console.log(response)
                if(response.data.token) {
                  localStorage.setItem("token", response.data.token)
                }
                Alert.alert('Log In Successful!')

            })
            .catch((error) => {
                console.log(error)
                Alert.alert("Log In Unsuccessful!")
            })
    }

    return (
    <SafeAreaView style = {styles.container}>
    <Text style = {styles.logo}>Repair Dukaan</Text>
      <View style = {styles.inputView}>
          <TextInput  
            style={styles.inputText}
            placeholder="Email" 
            placeholderTextColor="#003f5c"
            onChangeText = {setEmail}
          />
      </View>
      <View style = {styles.inputView}>
          <TextInput  
            style={styles.inputText}
            placeholder="Password" 
            placeholderTextColor="#003f5c"
            secureTextEntry = {true}
            onChangeText = {setPassword}
          />
      </View>
      <TouchableOpacity style={styles.loginBtn} onPress = {handleLogIn}>
          <Text style={styles.loginText}>LOGIN</Text>
      </TouchableOpacity>

      <TouchableOpacity>
          <Text style={styles.loginText}>Sign Up</Text>
      </TouchableOpacity>
      </SafeAreaView>
    )
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#003f5c',
        alignItems: 'center',
        justifyContent: 'center',
    },
    logo: {
        fontWeight:"bold",
        fontSize:50,
        color:"#fb5b5a",
        marginBottom:40,
      },
      inputView:{
        width:"80%",
        backgroundColor:"#465881",
        borderRadius:25,
        height:50,
        marginBottom:20,
        justifyContent:"center",
        padding:20
      },
      inputText:{
        height:50,
        color:"white"
      },
      loginBtn:{
        width:"80%",
        backgroundColor:"#fb5b5a",
        borderRadius:25,
        height:50,
        alignItems:"center",
        justifyContent:"center",
        marginTop:30,
        marginBottom:20
      },
      loginText:{
        color:"white"
      }
})
